import { createGlobalStyle } from 'styled-components';

export default createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    outline: 0;
    box-sizing: border-box;
  }

  body {
    background: white;
    -webkit-font-smoothing: antialiased;
    margin:0px;
    font-family: 'Roboto', sans-serif;
    background: url(${(props)=>props.imgUrl});  
  }

  body, input, button {
    font:16px;
  }

  #root{
    padding: 0px;
    height:100vh;
    background:black;
  }

`;
