import {
  GET_MY_ACCOUNT,
  GET_CURRENCY
} from './dashboardConstants';

const INITIAL_STATE = {
  myAccount: '',
  allCurrency:'',
  brl:'',
};

// eslint-disable-next-line import/no-anonymous-default-export
export default (state = INITIAL_STATE, action) => {
  // eslint-disable-next-line default-case
  switch (action.type) {
    case GET_MY_ACCOUNT:
      var _myAccount

      if(action.data){
        _myAccount = action.data
      }

      return {
        ...state,
        myAccount: _myAccount
      };

      case GET_CURRENCY:
        var _brl
        var _allCurrency

        if(action.data){
          _allCurrency = action.data
          _brl = action.data.ask
        }
        return {
          ...state,
          brl: parseFloat(_brl).toFixed(2),
          allCurrency: _allCurrency
        };
  }

  return state;
};
