import { useHistory } from "react-router-dom";
import { useState } from 'react'
//img
import bg from '../../../shared/assets/images/bg.jpg'

import api from '../../../core/api/api'

import {
  Containerbody,
  ContainerbodyOpacity
} from './dashboardStyle'

export function Dashboard() {
  const history = useHistory();

  //States
  const [comicsRecover, setComicsRecover] = useState([])
  console.log(comicsRecover)

  const handleChange = (text) => {
    api.get(`/characters?apikey=90477e77a3b01a078ec22b190e677c21&ts=1629003931&hash=36847f3d516088a7ce0d7e26491c4832&limit=40&offset=0&nameStartsWith=${text}`)
    .then((response) =>  setComicsRecover(response?.data?.results))
    .catch((err) => {
      console.error("ops! ocorreu um erro" + err);
    });
  }

  return (
    <Containerbody style={{backgroundImage: `url(${bg})`}}>
      <ContainerbodyOpacity>
        <h1>Marvel Quadrinhos</h1>
        <p>Pesquise pelos seus quadrinhos favoritos marvel</p>
        <input 
          placeholder='Pesquisar por heroi'
          onChange={(e) => handleChange(e.target.value)}
        />
        <p>{comicsRecover?.length}</p>
      </ContainerbodyOpacity>
    </Containerbody>
  );
}
