import styled from "styled-components";

export const Containerbody = styled.div`
  display:flex;
  height:100%;
`;

export const ContainerbodyOpacity = styled.div`
  display:flex;
  justify-content:flex-start;
  flex-direction:column;
  align-items:center;
  padding:20px;
  height:100%;
  width:100%;
  background:rgba(0,0,0,0.8);
  color:white;

  input {
    width:70%;
    padding:15px;
    border-radius:100px;
    border-width:0px;
    margin-top:20px;
  }

  h1 {
    font-size:4rem;
    color:white;
  }

  p{
    margin:15px;
    font-size:1.2rem;
  }
`;

export const ComicContainer = styled.div`
  height:180px;
  width:100%;
  background:red;
  max-width:145px;
`;
